import styled from 'styled-components';

export const Container = styled.div`
  display: flex;  
  align-items: center;
`;

export const Avatar = styled.img`
  //margin-bottom: 20px;
  width: 20px;
`;

export const Text = styled.p`
  font-weight: 600;
  margin: 5px 5px 0px;
`;

export const Button = styled.button`  
    width: 70px;
    font-size: 14px;
    padding: 5px;
    border: 3px solid rgb(63, 66, 77);
    border-radius: 4px;
    margin: 5px 5px 0px;
    display: flex;
    flex-direction: column;
`;
