import styled from 'styled-components';

export const Input = styled.input`
  border-radius: 4px;
  border: none;
  outline: 0px;
  background-color: rgb(255, 255, 255);

  width: 200px;
  padding: 12px;
  border: 3px solid #3f424d;
  border-radius: 4px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
`;
